import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';

const UploadWall = () => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState('');
  const [uploadMessage, setUploadMessage] = useState('');

  // Función para obtener la lista de documentos
  const fetchDocuments = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/documents/list');
      setDocuments(response.data);
      setLoading(false);
    } catch (error) {
      setError('Failed to fetch documents');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, []);

  // Función para manejar el cambio de archivo seleccionado
  const onChangeFile = (e) => {
    setFile(e.target.files[0]);
    setFilename(e.target.files[0].name);
  };

  // Función para manejar la subida del archivo
  const onSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      await axios.post('http://127.0.0.1:5000/documents/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setUploadMessage('File uploaded successfully');
      setFilename('');
      fetchDocuments(); // Actualizamos la lista de documentos después de la subida
    } catch (error) {
      setUploadMessage('File upload failed');
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h1>Document Management</h1>
      {/* Formulario de subida de archivos */}
      <form onSubmit={onSubmit}>
        <div>
          <input type="file" onChange={onChangeFile} />
        </div>
        <button type="submit">Upload</button>
      </form>
      {filename && <p>Selected File: {filename}</p>}
      {uploadMessage && <p>{uploadMessage}</p>}
      {/* Lista de documentos */}
      <h2>Document List</h2>
      <SearchBar setDocuments={setDocuments} />
      <ul>
        {documents.map((document) => (
          <li key={document._id}>
            <p>Filename: {document.filename}</p>
            <p>Author: {document.author}</p>
            <p>Category: {document.category}</p>
            <p>Upload Date: {new Date(document.uploadDate).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UploadWall;
