import React, { useState } from 'react';
import axios from 'axios';
//import { useNavigate } from 'react-router-dom';

const Upload = () => {
  const [file, setFile] = useState(null);
  const [author, setAuthor] = useState('');
  const [category, setCategory] = useState('');
  const [message, setMessage] = useState('');

  const onFileChange = (e) => setFile(e.target.files[0]);
  const onAuthorChange = (e) => setAuthor(e.target.value);
  const onCategoryChange = (e) => setCategory(e.target.value);

  const onSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);
    formData.append('author', author);
    formData.append('category', category);

    try {
      const response = await axios.post(`http://127.0.0.1:3000/documents/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response ? error.response.data.message : 'Failed to upload file');
    }
  };

  return (
    <div>
      <h1>Upload File</h1>
      <form onSubmit={onSubmit}>
        <input type="file" onChange={onFileChange} required />
        <input type="text" placeholder="Author" value={author} onChange={onAuthorChange} required />
        <input type="text" placeholder="Category" value={category} onChange={onCategoryChange} required />
        <button type="submit">Upload</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Upload;
